package sentenciasRepeticion;

public class ExplicacionWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Sentencias repetición while:
		//Variable de control: inicializarla , condición de continuidad , modificarla
		//Mostrar por pantalla los números del 1 al 50
		int control = 1;
		
		while(control <= 50) {
			System.out.print(control + " ");
			control+=2;
		}
		System.out.println();
		
	}

}
